import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Viewer, Worker, SpecialZoomLevel } from '@react-pdf-viewer/core';
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';
import '@react-pdf-viewer/core/lib/styles/index.css';
import '@react-pdf-viewer/default-layout/lib/styles/index.css';
import Navbar from './Navbar';
import '../styles.css';

const ViewPdf = () => {
  const { pdfId } = useParams();
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [pdfs, setPdfs] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPdfId, setSelectedPdfId] = useState(null);
  const [password, setPassword] = useState('');
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [passwordError, setPasswordError] = useState('');
  const [approvalToken, setApprovalToken] = useState(null);
  const [waitingApproval, setWaitingApproval] = useState(false);
  const searchInputRef = useRef(null);
  const role = localStorage.getItem('role');

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:5000/categories');
        if (!response.ok) throw new Error('Failed to fetch categories');
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };
    fetchCategories();
  }, []);

  useEffect(() => {
    if (selectedCategory) {
      const fetchPdfs = async () => {
        try {
          const response = await fetch(`http://localhost:5000/pdfs/${selectedCategory}`);
          if (!response.ok) throw new Error('Failed to fetch PDFs');
          const data = await response.json();
          setPdfs(data);
        } catch (error) {
          console.error('Error fetching PDFs:', error);
        }
      };
      fetchPdfs();
    } else {
      setPdfs([]);
    }
  }, [selectedCategory]);

  const renderToolbar = (Toolbar) => (
    <Toolbar>
      {(slots) => {
        const { CurrentPageInput, GoToNextPage, GoToPreviousPage, NumberOfPages, ZoomIn, ZoomOut } =
          slots;
        return (
          <div style={{ display: 'flex', alignItems: 'center', padding: '0 8px', flexWrap: 'wrap' }}>
            <GoToPreviousPage />
            <CurrentPageInput /> / <NumberOfPages />
            <GoToNextPage />
            <ZoomIn />
            <ZoomOut />
          </div>
        );
      }}
    </Toolbar>
  );

  const defaultLayoutPluginInstance = defaultLayoutPlugin({
    renderToolbar,
    toolbarPlugin: { downloadPlugin: { disabled: true }, printPlugin: { disabled: true }, openPlugin: { disabled: true } },
  });

  const selectedPdf = pdfId === 'all' ? null : pdfs.find((pdf) => pdf.id === Number(pdfId));
  const filteredPdfs = pdfs.filter((pdf) => pdf.name.toLowerCase().includes(searchQuery.toLowerCase()));

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setSearchQuery('');
    navigate('/view-pdf/all');
  };

  const handlePdfClick = (id) => {
    setSelectedPdfId(id);
    setShowPasswordModal(true);
    setPassword('');
    setPasswordError('');
    setWaitingApproval(false);
    if (searchInputRef.current) searchInputRef.current.blur();
  };

  const handlePasswordSubmit = async () => {
    try {
      const response = await fetch('http://localhost:5000/verify-pdf-access', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pdfId: selectedPdfId, password }),
      });
      const data = await response.json();

      if (!response.ok) {
        setPasswordError(data.error || 'Incorrect password');
        return;
      }

      setApprovalToken(data.token);
      setWaitingApproval(true);
      setShowPasswordModal(false);

      // Poll for approval
      const interval = setInterval(async () => {
        const checkResponse = await fetch(`http://localhost:5000/check-approval/${data.token}`);
        const checkData = await checkResponse.json();

        if (checkData.approved) {
          clearInterval(interval);
          setWaitingApproval(false);
          navigate(`/view-pdf/${selectedPdfId}`);
        } else if (checkData.error) {
          clearInterval(interval);
          setWaitingApproval(false);
          setPasswordError(checkData.error);
          setShowPasswordModal(true);
        }
      }, 2000); // Check every 2 seconds
    } catch (error) {
      console.error('Error verifying password:', error);
      setPasswordError('Something went wrong');
    }
  };

  const handleDelete = async (id) => {
    if (role === 'admin') {
      const pdfToDelete = pdfs.find((pdf) => pdf.id === id);
      if (!pdfToDelete) return;
      try {
        const updatedPdfs = pdfs.filter((pdf) => pdf.id !== id);
        setPdfs(updatedPdfs);
        navigate('/view-pdf/all');
      } catch (error) {
        console.error('Error deleting PDF:', error);
      }
    }
  };

  const handleClose = () => navigate('/view-pdf/all');

  return (
    <div className="view-pdf-container">
      <Navbar />
      <div className="view-pdf-content">
        {pdfId === 'all' ? (
          <>
            <h1 className="view-pdf-title">View PDFs</h1>
            {!selectedCategory ? (
              <div className="folder-grid">
                {categories.map((category) => (
                  <div key={category} className="folder-item" onClick={() => handleCategoryClick(category)}>
                    <div className="folder-icon"><span className="folder-label">{category}</span></div>
                  </div>
                ))}
              </div>
            ) : (
              <>
                <button className="back-button" onClick={() => setSelectedCategory(null)}>Back to Folders</button>
                <h2 className="category-title">{selectedCategory}</h2>
                <div className="search-bar">
                  <input
                    type="text"
                    name="search-pdf-random"
                    placeholder="Search PDFs by name..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                    autoComplete="new-password"
                    ref={searchInputRef}
                  />
                </div>
                <div className="pdf-grid">
                  {filteredPdfs.length > 0 ? (
                    filteredPdfs.map((pdf) => (
                      <div key={pdf.id} className="pdf-card-item" onClick={() => handlePdfClick(pdf.id)}>
                        <div className="pdf-icon"><span className="pdf-name">{pdf.name}</span></div>
                      </div>
                    ))
                  ) : (
                    <p className="no-results">No PDFs in this category</p>
                  )}
                </div>
              </>
            )}
          </>
        ) : selectedPdf ? (
          <div className="pdf-viewer-container">
            <div className="pdf-header">
              <h2 className="pdf-card-title">{selectedPdf.name}</h2>
              <div className="pdf-actions">
                <button className="close-button" onClick={handleClose}>Close</button>
                {role === 'admin' && (
                  <button className="delete-button" onClick={() => handleDelete(selectedPdf.id)}>Delete</button>
                )}
              </div>
            </div>
            <div className="pdf-viewer-wrapper">
              <Worker workerUrl="https://unpkg.com/pdfjs:dist@3.11.174/build/pdf.worker.min.js">
                <Viewer
                  fileUrl={selectedPdf.url}
                  defaultScale={SpecialZoomLevel.PageWidth}
                  plugins={[defaultLayoutPluginInstance]}
                  withCredentials={false}
                />
              </Worker>
            </div>
          </div>
        ) : (
          <p className="error-message">PDF not found</p>
        )}
      </div>

      {showPasswordModal && (
        <div className="password-modal-overlay">
          <div className="password-modal">
            <h3>Enter Password</h3>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              className="password-input"
              onKeyPress={(e) => e.key === 'Enter' && handlePasswordSubmit()}
            />
            {passwordError && <p className="password-error">{passwordError}</p>}
            <div className="modal-actions">
              <button className="submit-button" onClick={handlePasswordSubmit}>Submit</button>
              <button className="cancel-button" onClick={() => setShowPasswordModal(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {waitingApproval && (
        <div className="password-modal-overlay">
          <div className="password-modal">
            <h3>Waiting for Approval</h3>
            <p>Please wait for approval from the administrator.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default ViewPdf;
