import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles.css';

const Dashboard = () => {
  const [file, setFile] = useState(null);
  const [category, setCategory] = useState('OS');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const categories = ['OS', 'Networking', 'Database', 'Programming', 'Others'];

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setFile(e.dataTransfer.files[0]);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a file first!');
      return;
    }

    const formData = new FormData();
    formData.append('pdf', file);
    formData.append('category', category);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) throw new Error('Upload failed');
      const data = await response.json();
      setSuccessMessage(`File "${data.name}" uploaded to "${data.category}" successfully!`);
      setFile(null);
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Failed to upload file');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 to-indigo-100 text-gray-800">
      <Navbar />
      <div className="text-center py-8">
        <h1 className="text-4xl font-bold text-indigo-700 mb-2">Dashboard</h1>
        <p className="text-lg text-gray-600">Upload your PDFs here</p>
      </div>
      <div className="flex flex-col items-center justify-center px-4 pb-10">
        <div
          className={`w-full max-w-xl p-6 rounded-2xl shadow-xl bg-white transition-all duration-300 border-2 border-dashed ${
            file ? 'border-green-400' : 'border-indigo-300'
          }`}
          onDrop={handleDrop}
          onDragOver={handleDragOver}
        >
          <h3 className="text-2xl font-semibold text-center text-indigo-600 mb-6">Upload PDF</h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="flex flex-col">
              <label className="font-medium text-gray-700 mb-1">Category</label>
              <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-indigo-400"
              >
                {categories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>

            <label className="flex flex-col items-center justify-center border border-gray-300 rounded-lg py-4 px-6 cursor-pointer hover:bg-gray-100 transition">
              <span className="text-gray-600 font-medium">Choose File</span>
              <input
                type="file"
                accept="application/pdf"
                onChange={handleFileChange}
                className="hidden"
              />
            </label>

            <p className="text-sm text-gray-500 text-center">or drag and drop your PDF above</p>

            <button
              type="submit"
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-lg transition"
            >
              Upload
            </button>
          </form>

          {successMessage && (
            <p className="mt-4 text-green-600 text-sm text-center font-medium">{successMessage}</p>
          )}
          {file && (
            <p className="mt-2 text-indigo-500 text-sm text-center">Selected: {file.name}</p>
          )}
        </div>

        <button
          className="mt-8 bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-6 rounded-full shadow-lg transition"
          onClick={() => navigate('/view-pdf/all')}
        >
          View Uploaded PDFs
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
